package com.accusoft.tests.ocs.common.parsers;import java.io.InputStream;/** * The Class MaeMetricParser. */public class MaeMetricParser extends AbstractStreamParser {	/** The quantum range grp index. */	private static int QUANTUM_RANGE_GRP_INDEX = 1;	/** The relative quantum range grp index. */	private static int RELATIVE_QUANTUM_RANGE_GRP_INDEX = 2;	/** The mae metric pattern. */	public static String MAE_METRIC_PATTERN = "^([0-9]+\\.?[0-9]*)\\s+\\((.*)\\)";	/**	 * Instantiates a new mae metric parser.	 *	 * @param is the is	 */	public MaeMetricParser(InputStream is) {		super(is);	}	/* (non-Javadoc)	 * @see com.accusoft.sandbox.imagemagic.imagecomparator.parsers.AbstractStreamParser#getPattern()	 */	@Override	protected String getPattern() {		return MAE_METRIC_PATTERN;	}	/**	 * Gets the quantum range.	 *	 * @return the quantum range	 */	public float getQuantumRange() {		if (getMatcher() == null) {			parse();		}		return Float.parseFloat(getMatcher().group(QUANTUM_RANGE_GRP_INDEX));	}	/**	 * Gets the rel quantum range.	 *	 * @return the rel quantum range	 */	public float getRelQuantumRange() {		if (getMatcher() == null) {			parse();		}		return Float.parseFloat(getMatcher().group(				RELATIVE_QUANTUM_RANGE_GRP_INDEX));	}}